	    SFAppService.prototype.MakeCall = function (toAddress) {
	        this.ac.MakeCall(toAddress);
	    };
	    SFAppService.prototype.DoCallAction = function (action, data) {
	        if (data != null) {
	            this.logger.Debug('Agent Do Call Action : ' + action);
	            console.log("Agent Do Call Action : ", data);
	        }
	        switch (action) {
	            case 'ANSWER':
	                this.ac.AnswerCall(data.callId);
	                break;
	            case 'DROP':
	                this.ac.DropCall(data.callId);
	                break;
	            case 'HOLD':
	                this.ac.HoldCall(data.callId);
	                break;
	            case 'RETRIEVE':
	                this.ac.RetrieveCall(data.callId);
	                break;
	            case 'CONSULT':
	                this.ac.ConsultCall(data.callId, data.toAddress);
	                break;
	            case 'SST':
	                this.ac.DirectTransfer(data.callId, data.callState, data.agentId, data.userName);
	                break;
	            case 'TRANSFER':
	                this.ac.TransferCall(data.callId);
	                break;
	            case 'CONFERENCE':
	                this.ac.ConferenceCall(data.callId);
	                break;
	            case 'CONSULT_ACCEPT':
	                this.ac.AcceptConsultCall(data.callId);
	                break;
	            default:
	                console.log('AppService  action not processed ..', action);
	                break;
	        }
	    };
	    SFAppService.prototype.GetElapsedTime = function (time) {
	        return this.dt.SecondsToTime(time);
	    };
	    SFAppService.prototype.processChangeEvent = function (type, data) {
	        console.log('AppService processChangeEvent - ', type);
	        switch (type) {
	            case Models.Topics.AgentDetail:
	                //on agent detail received make a call for agent team information
	                this.onAgentDetailChanged(data);
	                break;
	            case Models.Topics.SystemInformation:
	                this.SystemInformation = data;
	                break;
	            case Models.Topics.CallDetail:
	                this.onCallDetailChanged(data);
	                break;
	            case Models.Topics.NotReadyReasonCode:
	                this.NotReadyReasonCode = data;
	                this.evnt.Broadcast(this.TopicList.NotReadyResonCode, this.NotReadyReasonCode);
	                break;
	            case Models.Topics.SystemMessage:
	                this.evnt.Broadcast(this.TopicList.SystemMessage, data);
	                break;
	            case Models.Topics.CallResult:
	                this.onCallResultChanged(data);
	                break;
	            default:
	                console.log('APP Service processChangeEvent type not processed :' + type);
	                break;
	        }
	    };
	    SFAppService.prototype.onAgentDetailChanged = function (agent) {
	        try {
	            console.log('%c APS onAgentDetailChanged() ', 'background:#FF334F; color:#FFF', agent);
	            var prevState = this.AgentDetail.State;
	            this.AgentDetail = agent;
	            this.store.SetData("Agent_State", agent.State);
	            this.logger.Debug("onAgentDetailChanged Agent State : " + this.AgentDetail.State + ' - Previous State : ' + prevState);
	            if (this.AgentDetail.State === "Available" || (this.AgentDetail.State === "Unavailable" && this.v.IsNotNullOrUndefinedOjbect(this.AgentDetail.Reason))) {
	                this.incident = null;
	                this.currentCallerSid = null;
	                this.currentSelectedIncident = null;
	            }
	            var self = this;
	            if (this.AgentDetail.State === "Unavailable") {
	                setTimeout(function () {
	                    console.log("Making outbound call");
	                }, 5000);
	            }
	            this.evnt.Broadcast(this.TopicList.AgentDetail, agent);
	        }
	        catch (ex) {
	            this.logger.Error('OnAgentState Change Error : ' + ex);
	        }
	    };
	    SFAppService.prototype.onCallDetailChanged = function (calls) {
	        console.log('%c APS - onCallDetailChanged :', 'background:#FF334F; color:#FFF', calls);
	        var lstCallId = {};
	        this.CallDetails = calls;
	        if (this.CallDetails.length > 0) {
	            if (this.Call === null || this.CallDetails.length === 1) {
	                this.Call = this.CallDetails[0];
	            }
	            else {
	                for (var i = 0; i < this.CallDetails.length; i++) {
	                    if (this.CallDetails[i].Id == this.Call.Id) {
	                        this.Call = this.CallDetails[i];
	                        break;
	                    }
	                }
	            }
	        }
	        else {
	            this.Call = null;
	            this.setActiveCallResult();
	        }
	        this.evnt.Broadcast(this.TopicList.CurrentCallDetail, this.Call);
	        this.evnt.Broadcast(this.TopicList.CallDetails, this.CallDetails); // Bin Code 
