	    LCMConnector.prototype.onStorageUpdate = function (key, value) {
	        //console.log('Finesse Storage updated --', key, ' : ', value);
	        var data = JSON.parse(value);
	        console.log('LCM method - on storage update ', key, ' : ', value);
	        switch (key) {
	            case Models.LCMTopics.LCMKeyInformation:
	                this.lcmKeyInformation = data;
	                this.evnt.Broadcast(Models.LCMTopics.LCMKeyInformation, data);
	                break;
	            case Models.LCMTopics.LCMModes:
	                this.lcmModes = data;
	                //trigger the event from here
	                this.evnt.Broadcast(Models.LCMTopics.LCMModes, data);
	                break;
	            case Models.LCMTopics.LCMCallbackModes:
	                this.lcmCallbackModes = data;
	                //trigger the event from here      
	                this.evnt.Broadcast(Models.LCMTopics.LCMCallbackModes, data);
	                break;
	            case Models.LCMTopics.LCMBusinessFields:
	                this.lcmBusinessFields = data;
	                this.evnt.Broadcast(Models.LCMTopics.LCMBusinessFields, data);
	                break;
	            case Models.LCMTopics.LCMBOGroupParents:
	                this.lcmBOGroupParents = data;
	                //trigger the event from here      
	                this.evnt.Broadcast(Models.LCMTopics.LCMBOGroupParents, data);
	                break;
	            case Models.LCMTopics.LCMBusinessOutComes:
	                this.lcmBusinessOutComes = data;
	                this.evnt.Broadcast(Models.LCMTopics.LCMBusinessOutComes, data);
	                break;
	            case Models.LCMTopics.LCMCallOutComes:
	                this.lcmCallOutComes = data;
	                //trigger the event from here      
	                this.evnt.Broadcast(Models.LCMTopics.LCMCallOutComes, data);
	                break;
	            case Models.LCMTopics.LCMCallCount:
	                // if (this.isUCCX === true) {
	                this.lcmCallCount = data;
	                //trigger the event from here                      
	                this.evnt.Broadcast(Models.LCMTopics.LCMCallCount, this.lcmCallCount);
	                //}
	                break;
	            case Models.LCMTopics.LCMContact:
	                // if (this.isUCCX === true) {
	                this.lcmContact = data;
	                //trigger the event from here                      
	                this.evnt.Broadcast(Models.LCMTopics.LCMContact, this.lcmContact);
	                // }
	                break;
	            default:
	                console.log('LCM method - on storage update not processed ', key, ' : ', value);
	                break;
	        }
