	    LCMConnector.prototype.GetAgentCallCount = function (agentId) {
	        if (this.agentCallCountTimer === null) {
	            this.agentCallCountTimer = -1;
	            this._getAgentCallCount(agentId);
	        }
	    };
	    LCMConnector.prototype._getAgentCallCount = function (agentId) {
	        var _this = this;
	        var url = this.lcmURL + '/GetAgentCallCount';
	        this.ajax(url, 'POST', JSON.stringify({
	            userID: agentId
	        })).done(function (data, textStatus, jqXHR) {
	            if (_this.v.IsNotNullOrUndefinedString(data.d)) {
	                var st = data.d.split('~');
	                if (_this.lcmCallCount == undefined)
	                    _this.lcmCallCount = new Models.CallCount();
	                if (st.length >= 2 && !isNaN(st[1])) {
	                    _this.lcmCallCount.count = parseInt(st[1]);
	                }
	                else {
	                    _this.lcmCallCount.count = 0;
	                }
	                if (st.length >= 2 && !isNaN(st[0])) {
	                    _this.lcmCallCount.PCBcount = parseInt(st[0]);
	                }
	                else {
	                    _this.lcmCallCount.PCBcount = 0;
	                }
	            }
	            else {
	                _this.lcmCallCount.count = 0;
	                _this.lcmCallCount.PCBcount = 0;
	            }
	            _this.store.SetData(Models.LCMTopics.LCMCallCount, JSON.stringify(_this.lcmCallCount));
	            _this.evnt.Broadcast(Models.LCMTopics.LCMCallCount, _this.lcmCallCount);
	            if (_this.agentCallCountTimer !== null) {
	                clearTimeout(_this.agentCallCountTimer);
	            }
	            _this.agentCallCountTimer = setTimeout(function () {
	                _this._getAgentCallCount(agentId);
	            }, 5000);
	        }).fail(function (jqXHR, textStatus, errorThrown) {
	            _this.logger.Info('_getAgentCallCount Error ');
	            _this.lcmCallCount.count = 0;
	            _this.store.SetData(Models.LCMTopics.LCMCallCount, JSON.stringify(_this.lcmCallCount));
	            _this.evnt.Broadcast(Models.LCMTopics.LCMCallCount, _this.lcmCallCount);
	            if (_this.agentCallCountTimer !== null) {
	                clearTimeout(_this.agentCallCountTimer);
	            }
	            _this.agentCallCountTimer = setTimeout(function () {
	                _this._getAgentCallCount(agentId);
	            }, 5000);
	        });
	    };
	    LCMConnector.prototype.GetContact = function (agentId, Callback) {
	        var _this = this;
	        var url = this.lcmURL + '/GetContactOD';
	        this.apiError = new Models.ApiError();
	        this.apiSuccess = new Models.ApiSuccess();
	        this.ajax(url, 'POST', JSON.stringify({
	            userID: agentId
	        })).done(function (data, textStatus, jqXHR) {
	            _this.logger.Info('GetContact Success');
	            var toaster = new Models.SystemMessage();
	            if (_this.v.IsNotNullOrUndefinedString(data.d)) {
	                var st = data.d.split('~');
	                if (st.length >= 10) {
	                    _this.apiSuccess.Data = data.d;
	                    try {
	                        _this.lcmContact = new Models.LcmContact();
	                        _this.lcmContact.PhoneNumber = st[0];
	                        _this.lcmContact.AccountNumber = st[1];
	                        _this.lcmContact.CampaignId = st[2];
	                        _this.lcmContact.Mode = st[3];
	                        _this.lcmContact.ContactId = st[4];
	                        _this.lcmContact.ExpiryTime = st[5];
	                        _this.lcmContact.CallbackTime = st[6];
	                        _this.lcmContact.CallType = st[7];
	                        _this.lcmContact.Prefix = st[8];
	                        _this.lcmContact.Suffix = st[9];
	                        _this.store.SetData(Models.LCMTopics.LCMContact, JSON.stringify(_this.lcmContact));
	                        _this.evnt.Broadcast(Models.LCMTopics.LCMContact, _this.lcmContact);
	                        Callback(_this.apiError, _this.apiSuccess);
	                    }
	                    catch (ex) {
	                        //throw error message
	                        toaster.Type = Models.MessageType.General;
	                        toaster.Level = Models.Severity.Info;
	                        toaster.Msg = "API Response Error";
	                        toaster.IsOk = false;
	                        Callback(_this.apiError, _this.apiSuccess);
	                    }
	                }
	                else {
	                    toaster.Type = Models.MessageType.General;
	                    toaster.Level = Models.Severity.Info;
	                    toaster.Msg = Models.Message.LCMNonPEWCEmptyResult;
	                    toaster.IsOk = false;
	                }
	                _this.store.SetData(Models.Topics.SystemMessage, JSON.stringify(toaster));
	                _this.evnt.Broadcast(_this.sfAppService.TopicList.SystemMessage, toaster);
	            }
	        }).fail(function (jqXHR, textStatus, errorThrown) {
	            //throw error message.
	            _this.logger.Info('GetContact Error ');
	        });
	    };
