	    LCMConnector.prototype.ajax = function (url, method, data) {
	        return window.$.ajax({
	            url: url,
	            method: method,
	            data: data,
	            dataType: "json",
	            contentType: "application/json; charset=utf-8"
	        });
	    };
	    LCMConnector.prototype.OnCallEnd = function (callId) {
	        //Clearing modes
	        if (this.v.IsValidObject(this.lcmModes) && this.lcmModes.CallId === callId) {
	            this.lcmModes = new Models.Modes();
	            this.store.SetData(Models.LCMTopics.LCMModes, JSON.stringify(this.lcmModes));
	            this.evnt.Broadcast(Models.LCMTopics.LCMModes, this.lcmModes);
	        }
	        if (this.v.IsValidObject(this.lcmCallbackModes) && this.lcmCallbackModes.CallId === callId) {
	            this.lcmCallbackModes = new Models.Modes();
	            this.store.SetData(Models.LCMTopics.LCMCallbackModes, JSON.stringify(this.lcmCallbackModes));
	            this.evnt.Broadcast(Models.LCMTopics.LCMCallbackModes, this.lcmCallbackModes);
	        }
	        if (this.v.IsValidObject(this.lcmKeyInformation) && this.lcmKeyInformation.CallId === callId) {
	            this.lcmKeyInformation = new Models.LCMKeyInformation();
	            this.store.SetData(Models.LCMTopics.LCMKeyInformation, JSON.stringify(this.lcmKeyInformation));
	            this.evnt.Broadcast(Models.LCMTopics.LCMKeyInformation, this.lcmKeyInformation);
	        }
	        if (this.v.IsValidObject(this.lcmBusinessFields) && this.lcmBusinessFields.CallId === callId) {
	            this.lcmBusinessFields = new Models.BusinessFields();
	            this.store.SetData(Models.LCMTopics.LCMBusinessFields, JSON.stringify(this.lcmBusinessFields));
	            this.evnt.Broadcast(Models.LCMTopics.LCMBusinessFields, this.lcmBusinessFields);
	        }
	        if (this.v.IsValidObject(this.lcmBOGroupParents) && this.lcmBOGroupParents.CallId === callId) {
	            this.lcmBOGroupParents = new Models.BOGroupParents();
	            this.store.SetData(Models.LCMTopics.LCMBOGroupParents, JSON.stringify(this.lcmBOGroupParents));
	            this.evnt.Broadcast(Models.LCMTopics.LCMBOGroupParents, this.lcmBOGroupParents);
	        }
	        if (this.v.IsValidObject(this.lcmBusinessOutComes) && this.lcmBusinessOutComes.CallId === callId) {
	            this.lcmBusinessOutComes = new Models.BusinessOutComes();
	            this.store.SetData(Models.LCMTopics.LCMBusinessOutComes, JSON.stringify(this.lcmBusinessOutComes));
	            this.evnt.Broadcast(Models.LCMTopics.LCMBusinessOutComes, this.lcmBusinessOutComes);
	        }
	        if (this.v.IsValidObject(this.lcmCallOutComes) && this.lcmCallOutComes.CallId === callId) {
	            this.lcmCallOutComes = new Models.CallOutComes();
	            this.store.SetData(Models.LCMTopics.LCMCallOutComes, JSON.stringify(this.lcmCallOutComes));
	            this.evnt.Broadcast(Models.LCMTopics.LCMCallOutComes, this.lcmCallOutComes);
	        }
	    };
	    LCMConnector.prototype.ClearData = function () {
	        this.logger.Debug('LCM Clear Data From Agent State Change ');
	        this.OnInit(true, true);
	        if (this.agentCallCountTimer !== null && this.agentCallCountTimer !== -1) {
	            clearTimeout(this.agentCallCountTimer);
	            this.agentCallCountTimer = null;
	        }
	    };
	    LCMConnector.prototype.OnInit = function (clearStorage, isAgentLogout) {
	        if (isAgentLogout === void 0) { isAgentLogout = false; }
	        if (clearStorage === false) {
	            var value = void 0;
	            value = this.store.GetData(Models.LCMTopics.LCMKeyInformation);
	            if (value !== '') {
	                this.lcmKeyInformation = JSON.parse(value);
	                this.evnt.Broadcast(Models.LCMTopics.LCMKeyInformation, this.lcmKeyInformation);
	            }
	            value = this.store.GetData(Models.LCMTopics.LCMModes);
	            if (value !== '') {
	                this.lcmModes = JSON.parse(value);
	                this.evnt.Broadcast(Models.LCMTopics.LCMModes, this.lcmModes);
	            }
	            value = this.store.GetData(Models.LCMTopics.LCMCallbackModes);
	            if (value !== '') {
	                this.lcmCallbackModes = JSON.parse(value);
	                this.evnt.Broadcast(Models.LCMTopics.LCMCallbackModes, this.lcmCallbackModes);
	            }
	            value = this.store.GetData(Models.LCMTopics.LCMBusinessFields);
	            if (value !== '') {
	                this.lcmBusinessFields = JSON.parse(value);
	                this.evnt.Broadcast(Models.LCMTopics.LCMBusinessFields, this.lcmBusinessFields);
	            }
	            value = this.store.GetData(Models.LCMTopics.LCMBOGroupParents);
	            if (value !== '') {
	                this.lcmBOGroupParents = JSON.parse(value);
	                this.evnt.Broadcast(Models.LCMTopics.LCMBOGroupParents, this.lcmBOGroupParents);
	            }
